CREATE TABLE `department_assigns` (  `id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  `user_id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  `department_id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE `departments` (  `id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  `department` varchar(100) COLLATE utf8_unicode_ci NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE `discount_codes` (  `id` varchar(13) NOT NULL,  `code` varchar(100) NOT NULL,  `discount_type` varchar(50) NOT NULL,  `discount_amount` int(10) NOT NULL,  `code_limit` int(11) NOT NULL,  `max_products` int(5) NOT NULL,  `allowed_products` varchar(200) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE `email_recievers` (  `id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  `user_id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  `mail_id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE `emails` (  `id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  `email` varchar(100) COLLATE utf8_unicode_ci NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE `graduating` (  `id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  `product_id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  `amount` int(11) NOT NULL,  `price_per_item` float NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE `items` (  `id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  `name` varchar(200) COLLATE utf8_unicode_ci NOT NULL,  `number` varchar(50) COLLATE utf8_unicode_ci NOT NULL,  `description` text COLLATE utf8_unicode_ci NOT NULL,  `unit` varchar(10) COLLATE utf8_unicode_ci NOT NULL,  `price` float NOT NULL,  `price_per_amt` int(5) NOT NULL,  `order_size` int(5) NOT NULL,  `material` varchar(20) COLLATE utf8_unicode_ci NOT NULL,  `weight` float NOT NULL,  `geometry` varchar(20) COLLATE utf8_unicode_ci NOT NULL,  `size` varchar(100) COLLATE utf8_unicode_ci NOT NULL,  `comment` text COLLATE utf8_unicode_ci NOT NULL,  `alert_amt` int(5) NOT NULL,  `stock` int(5) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE `language_lib` (  `keyword` varchar(255) COLLATE utf8_unicode_ci NOT NULL,  `DE` varchar(255) COLLATE utf8_unicode_ci NOT NULL,  `EN` varchar(255) COLLATE utf8_unicode_ci NOT NULL,  PRIMARY KEY (`keyword`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE `product_contains` (  `id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  `parent` varchar(50) COLLATE utf8_unicode_ci NOT NULL,  `child` varchar(50) COLLATE utf8_unicode_ci NOT NULL,  `quantity` float NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE `products` (  `number` varchar(50) COLLATE utf8_unicode_ci NOT NULL,  `name` varchar(100) COLLATE utf8_unicode_ci NOT NULL,  `description` text COLLATE utf8_unicode_ci NOT NULL,  `unit` varchar(10) COLLATE utf8_unicode_ci NOT NULL,  `price` float NOT NULL,  `per_item` float NOT NULL,  `order_size` float NOT NULL,  `production_company` varchar(150) COLLATE utf8_unicode_ci NOT NULL,  `retail_company` varchar(150) COLLATE utf8_unicode_ci NOT NULL,  `link` text COLLATE utf8_unicode_ci NOT NULL,  `comment` text COLLATE utf8_unicode_ci NOT NULL,  `resell_price` float NOT NULL,  `attributes` text COLLATE utf8_unicode_ci NOT NULL,  PRIMARY KEY (`number`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE `protocol` (  `id` varchar(13) CHARACTER SET latin1 NOT NULL,  `user` varchar(13) CHARACTER SET latin1 NOT NULL,  `date` varchar(50) CHARACTER SET latin1 NOT NULL,  `description` text CHARACTER SET latin1 NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE `settings` (  `setting` varchar(200) NOT NULL,  `value` varchar(200) NOT NULL,  PRIMARY KEY (`setting`)) ENGINE=InnoDB DEFAULT CHARSET=latin1;
