CREATE TABLE `timestamp` (  `id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  `user_id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  `come` datetime NOT NULL,  `go` datetime NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
CREATE TABLE `users` (  `id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  `first_name` varchar(50) COLLATE utf8_unicode_ci NOT NULL,  `last_name` varchar(50) COLLATE utf8_unicode_ci NOT NULL,  `email` varchar(100) COLLATE utf8_unicode_ci NOT NULL,  `password` varchar(100) COLLATE utf8_unicode_ci NOT NULL,  `stamped` tinyint(1) NOT NULL,  `current_ts_id` varchar(13) COLLATE utf8_unicode_ci NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
